package calculator;

public class Addition extends Number{

	public Addition() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Addition(int num1, int num2) {
		super(num1, num2);
		// TODO Auto-generated constructor stub
	}
	
	public int addResult() {
		return num1 + num2;
	}
	
	public int subResult() {
		return num1 + num2;
	}
	
	public int mulResult() {
		return num1 * num2;
	}
	
	public int divResult() {
		return num1 / num2;
	}
}
